import React, { useLayoutEffect, useEffect, useState } from "react";
import {
  View,
  Text,
  Button,
  Image,
  Dimensions,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
} from "react-native";
import axios from "axios";
import { ShopDetailTitle } from "../../assets/common/common";
import Icon from "react-native-vector-icons/MaterialCommunityIcons";
import { createStackNavigator } from "@react-navigation/stack";
import { SwiperFlatList } from "react-native-swiper-flatlist";

export const photoShopDetail = ({ route, navigation }) => {
  navigation.setOptions({
    tabBarIcon: ({ focused, tintColor }) => {
      return null;
    },
    tabBarLabel: ({ focused, tintColor }) => {
      return null;
    },
  });

  const row = route.params.row;
  const colors = [
    `${row.present_img}`,
    `${row.sub_img_1}`,
    `${row.sub_img_2}`,
    `${row.sub_img_3}`,
    `${row.sub_img_4}`,
  ];
  const userWidth = Dimensions.get("window").width;

  return (
    <ScrollView style={{ backgroundColor: "white" }}>
      <SwiperFlatList
        data={colors}
        onChangeIndex={({ index, prevIndex }) => {}}
        renderItem={({ item }) => (
          <View style={[{ backgroundColor: "black" }]}>
            <Image
              style={{ width: userWidth, height: 450 }}
              source={{ uri: item }}
            />
          </View>
        )}
      />
      <View style={{ paddingLeft: 12 }}>
        <ShopDetailTitle>{row.title}</ShopDetailTitle>
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            backgroundColor: "#c5e1a5",
            width: 300,
            padding: 8,
            marginTop: 12,
            borderRadius: 10,
          }}
        >
          <Text>
            <View>
              <Text style={{ color: "#424242" }}>
                <Icon name="map-marker-radius-outline" size={15} /> {row.local}
              </Text>
            </View>
            {"\n"}
            <View style={{ marginTop: 15 }}>
              <Text style={{ color: "#424242" }}>
                <Icon name="clock-outline" size={15} /> 매일 10시~9시
              </Text>
            </View>
          </Text>
        </View>
        <Text style={{ marginTop: 12, color: "#757575", lineHeight: 20 }}>
          {row.intro}
        </Text>
        <View style={{ flexDirection: "row", justifyContent: "center" }}>
          <TouchableOpacity
            style={{ width: 100, height: 30, backgroundColor: "blue" }}
          >
            <Text>전화하기</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={{ width: 100, height: 30, backgroundColor: "green" }}
          >
            <Text>예약하기</Text>
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
};
