import React, { useLayoutEffect, useEffect, useState } from "react";
import { View, Text, Button, Image } from "react-native";
import axios from "axios";
import { Con, Card, SafeCon, ListCard } from "../assets/common/common";
import { SafeAreaView } from "react-native-safe-area-context";
import Icon from "react-native-vector-icons/FontAwesome";
import styled from "styled-components/native";

const Home = ({ navigation }) => {
  const [photoShops, setPhotoShops] = useState();

  useEffect(() => {
    axios({
      method: "get",
      url: "http://172.30.1.25:4000/getPhotoShop",
      responseType: "json",
    })
      .then(({ data, status }) => {
        if (status == 200) setPhotoShops(data);
      })
      .catch((res) => {
        console.log(res);
      });
  }, []);

  return (
    <SafeCon>
      <ListCard>
        {photoShops &&
          photoShops.map((val, idx) => {
            return (
              <Card key={val.seq}>
                <View style={{ flexDirection: "row" }}>
                  <Image
                    style={{ width: 200, height: 200, borderRadius: 6 }}
                    source={{
                      uri: `https://search.pstatic.net/common/?autoRotate=true&quality=95&type=w750&src=https%3A%2F%2Fldb-phinf.pstatic.net%2F20210805_19%2F1628127073058OCARu_JPEG%2F131.jpg`,
                    }}
                  />
                  <View style={{ marginLeft: 10 }}>
                    <Text>{val.title}</Text>
                    <Text>{val.local}</Text>
                    <Text>매일 10시~9시</Text>
                  </View>
                </View>
              </Card>
            );
          })}
      </ListCard>
      <Button title="zz"></Button>
    </SafeCon>
  );
};

export default Home;
