import React, { useState, useRef, useEffect, useLayoutEffect } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  Image,
  TextInput,
  Dimensions,
  Button,
} from "react-native";
import axios from "axios";
import { Con, SafeCon } from "../assets/common/common";
import { SafeAreaView } from "react-native-safe-area-context";
import { createStackNavigator } from "@react-navigation/stack";
import { NavigationContainer } from "@react-navigation/native";
import { ScrollView } from "react-native-gesture-handler";
import { timeForToday } from "../assets/helper/timeHelper";
import Port from "./Port";

const ChatList = ({ route, navigation, data }) => {
  console.log(route);

  const [chatRoom, setChatRoom] = useState();

  useEffect(() => {
    axios({
      method: "get",
      url: `${Port}/getChatRoom`,
      params: {
        seq: 1,
      },
    })
      .then(({ data, status }) => {
        if (status === 200) setChatRoom(data);
      })
      .catch((e) => alert(e));
  }, []);

  return (
    <ScrollView>
      {chatRoom &&
        chatRoom.map((val, idx) => {
          return (
            <TouchableOpacity
              key={val.seq}
              onPress={() => navigation.navigate("ChatDetail", { val })}
            >
              <View
                style={{
                  flexDirection: "row",
                  padding: 15,
                  borderBottomColor: "#e0e0e0",
                  borderBottomWidth: 1,
                }}
              >
                <Image
                  style={{ width: 50, height: 50, borderRadius: 50 }}
                  source={{
                    uri: "https://file.mk.co.kr/meet/neds/2021/04/image_readtop_2021_330747_16177500644599916.jpg",
                  }}
                />
                <View
                  style={{
                    marginLeft: 12,
                    justifyContent: "space-around",
                  }}
                >
                  <View style={{ flexDirection: "row" }}>
                    <Text style={{ fontSize: 12, color: "#4caf50" }}>
                      {val.title}
                    </Text>
                    <Text
                      style={{
                        fontSize: 12,
                        color: "#9e9e9e",
                        marginLeft: 6,
                      }}
                    >
                      {timeForToday(val.reg_date)}
                    </Text>
                  </View>
                  <View>
                    <Text>{val.msg}</Text>
                  </View>
                </View>
              </View>
            </TouchableOpacity>
          );
        })}
    </ScrollView>
  );
};

const ChatDetail = ({ route, navigation }) => {
  const val = route.params.val;
  const [msgList, setMsgList] = useState();
  const [inputMsg, setInputMsg] = useState();
  const loginUserNo = 2;
  const userWidth = Dimensions.get("window").width;
  const scrollViewRef = useRef();
  const inputRef = useRef();

  useEffect(() => {
    axios({
      method: "get",
      url: `${Port}/getChatMsg`,
      params: { room_seq: val.room_seq },
    })
      .then(({ data, status }) => {
        if (status === 200) setMsgList(data);
      })
      .catch((e) => {
        alert(e);
      });
  }, []);

  const saveMsg = async () => {
    if (inputMsg?.length === 0) {
      alert("메시지를 입력해주세요.");
      return;
    }

    const now = new Date();
    const msg = {
      is_read: "N",
      msg: inputMsg,
      receive_user: val.receive_user,
      receive_user_name: val.receive_user_name,
      reg_date: val.reg_date,
      room_seq: val.room_seq,
      send_user: loginUserNo,
      send_user_name: val.send_user_name,
      seq: now.getTime(),
      title: val.title,
    };

    const cloneList = [...msgList];
    cloneList.push(msg);
    setMsgList(cloneList);
    setInputMsg("");

    await axios({
      method: "get",
      url: `${Port}/getInsertMsg`,
      params: {
        msg: inputMsg,
        roomSeq: val.room_seq,
        sendUser: loginUserNo,
        receiveUser: val.receive_user,
        isRead: val.isRead,
      },
    })
      .then(({ data }) => {
        console.log(data);
      })
      .catch((e) => alert(e));
  };

  return (
    <Con>
      <ScrollView
        ref={scrollViewRef}
        onContentSizeChange={() =>
          scrollViewRef.current.scrollToEnd({ animated: true })
        }
      >
        {msgList &&
          msgList.map((val, idx) => {
            return (
              <View
                key={val.seq}
                style={{
                  flexDirection: "row",
                  padding: 15,
                }}
              >
                {loginUserNo === val.send_user ? (
                  <View
                    style={{
                      flex: 1,
                      flexDirection: "row",
                      justifyContent: "flex-end",
                    }}
                  >
                    <View
                      style={{
                        backgroundColor: "#a5d6a7",
                        borderRadius: 12,
                        marginRight: 12,
                        padding: 12,
                      }}
                    >
                      <Text style={{ color: "black" }}>{val.msg}</Text>
                      {/* <Text>{timeForToday(val.reg_date)}</Text> */}
                    </View>
                    <Image
                      style={{ width: 50, height: 50, borderRadius: 50 }}
                      source={{
                        uri: "https://cloudfront-ap-northeast-1.images.arcpublishing.com/chosun/GRUY3S3TLKVUE5O2IR7OKFK4O4.jpg",
                      }}
                    />
                  </View>
                ) : (
                  <View
                    style={{
                      flex: 1,
                      flexDirection: "row",
                    }}
                  >
                    <Image
                      style={{ width: 50, height: 50, borderRadius: 50 }}
                      source={{
                        uri: "https://cdn.imweb.me/thumbnail/20210114/57a0baa52d786.jpg",
                      }}
                    />
                    <View
                      style={{
                        backgroundColor: "#424242",
                        borderRadius: 12,
                        marginLeft: 12,
                        padding: 12,
                      }}
                    >
                      <Text style={{ color: "white" }}>{val.msg}</Text>
                    </View>
                  </View>
                )}
              </View>
            );
          })}
      </ScrollView>
      <View style={{ height: 60 }}></View>
      <View
        style={{
          position: "absolute",
          height: 60,
          bottom: 0,
          width: userWidth,
          justifyContent: "center",
          padding: 12,
          backgroundColor: "#424242",
        }}
      >
        <View style={{ flexDirection: "row" }}>
          <TextInput
            onChangeText={(text) => {
              setInputMsg(text);
            }}
            ref={inputRef}
            placeholder="메시지"
            value={inputMsg}
            placeholderTextColor="#ffffff"
            fontSize={15}
            style={{
              color: "white",
              width: userWidth - 70,
            }}
          ></TextInput>
          <TouchableOpacity
            onPress={saveMsg}
            style={{
              padding: 12,
              backgroundColor: "white",
              borderRadius: 6,
            }}
          >
            <Text>전송</Text>
          </TouchableOpacity>
        </View>
      </View>
    </Con>
  );
};

const Stack = createStackNavigator();

const Chat = ({ route, navigation }) => {
  const navigationState = navigation.getState();
  useLayoutEffect(() => {
    const isDetail =
      navigationState?.routes[1]?.state?.index === 1 ? "none" : null;

    navigation.setOptions({
      tabBarStyle: { display: isDetail },
    });
  }, [navigationState]);

  return (
    <Stack.Navigator screenOptions={{ headerShown: false }}>
      <Stack.Screen
        name="ChatList"
        component={ChatList}
        params={{ id: "zz??" }}
      />
      <Stack.Screen name="ChatDetail" component={ChatDetail} />
    </Stack.Navigator>
  );
};

export default Chat;
