import React, { useLayoutEffect, useEffect, useState } from "react";
import { View, Text, Button, Image, Dimensions } from "react-native";
import axios from "axios";
import {
  Con,
  Card,
  SafeCon,
  ListCard,
  ShopDes,
  ShopTitle,
} from "../assets/common/common";
import { SafeAreaView } from "react-native-safe-area-context";
import Icon from "react-native-vector-icons/FontAwesome";
import styled, { withTheme } from "styled-components/native";
import { Rating, AirbnbRating } from "react-native-ratings";

const Home = ({ navigation }) => {
  const [photoShops, setPhotoShops] = useState();

  const getTextLength = function (str) {
    var len = 0;
    for (var i = 0; i < str.length; i++) {
      if (escape(str.charAt(i)).length == 6) {
        len++;
      }
      len++;
    }
    return len;
  };

  useEffect(() => {
    axios({
      method: "get",
      url: "http://172.30.1.25:4001/getPhotoShop",
      responseType: "json",
    })
      .then(({ data, status }) => {
        if (status == 200) setPhotoShops(data);
      })
      .catch((res) => {
        console.log(res);
      });
  }, []);

  return (
    <SafeCon>
      <ListCard>
        {photoShops &&
          photoShops.map((val, idx) => {
            return (
              <Card key={val.seq}>
                <View style={{ flexDirection: "row" }}>
                  <Image
                    style={{ width: 150, height: 150, borderRadius: 6 }}
                    source={{
                      uri: `${val.present_img}`,
                    }}
                  />
                  <View style={{ marginLeft: 10, alignItems: "baseline" }}>
                    <ShopTitle>{val.title}</ShopTitle>
                    <ShopDes>{val.local}</ShopDes>
                    <ShopDes>매일 10시~9시</ShopDes>
                    <AirbnbRating
                      type="custom"
                      reviews={["Terrible"]}
                      ratingBackgroundColor="#c8c7c8"
                      ratingCount={5}
                      defaultRating={val.satis_avg}
                      size={20}
                    />
                  </View>
                </View>
              </Card>
            );
          })}
      </ListCard>
      <Button title="zz"></Button>
    </SafeCon>
  );
};

export default Home;
