import React, { useLayoutEffect, useEffect, useState } from "react";
import {
  View,
  Text,
  Button,
  Image,
  Dimensions,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  Linking,
} from "react-native";
import axios from "axios";
import {
  ShopDetailTitle,
  FixedBottomBtn,
  FixedBtn,
} from "../../assets/common/common";
import Icon from "react-native-vector-icons/MaterialCommunityIcons";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createStackNavigator } from "@react-navigation/stack";
import { SwiperFlatList } from "react-native-swiper-flatlist";

const FixedBottom = () => {
  return (
    <FixedBottomBtn>
      <FixedBtn>
        <Text style={{ fontSize: 18, color: "#fafafa", fontWeight: "bold" }}>
          사진 전송 예약
        </Text>
      </FixedBtn>
      <FixedBtn>
        <Text style={{ fontSize: 18, color: "#fafafa", fontWeight: "bold" }}>
          방문 예약
        </Text>
      </FixedBtn>
    </FixedBottomBtn>
  );
};

export const photoShopDetail = ({ navigation, route }) => {
  const [like, setLike] = useState(0);
  const row = route.params.row;
  const userWidth = Dimensions.get("window").width;
  const presentImg = [
    `${row.present_img}`,
    `${row.sub_img_1}`,
    `${row.sub_img_2}`,
    `${row.sub_img_3}`,
    `${row.sub_img_4}`,
  ];

  const handleChat = (seq) => {
    navigation.navigate("Chat", { seq });
  };

  return (
    <View>
      <ScrollView style={{ backgroundColor: "white" }}>
        <SwiperFlatList
          data={presentImg}
          onChangeIndex={({ index, prevIndex }) => {}}
          renderItem={({ item }) => (
            <View style={[{ backgroundColor: "black" }]}>
              <Image
                style={{ width: userWidth, height: 450 }}
                source={{ uri: item }}
              />
            </View>
          )}
        />
        <View
          style={{
            paddingLeft: 12,
            paddingRight: 12,
          }}
        >
          <ShopDetailTitle>{row.title}</ShopDetailTitle>
          <View
            style={{
              flexDirection: "row",
              width: userWidth - 30,
              justifyContent: "space-evenly",
              padding: 12,
              marginTop: 8,
              borderRadius: 6,
              elevation: 3,
              backgroundColor: "#fff",
              shadowOffset: { width: 1, height: 1 },
              shadowOpacity: 0.3,
              marginVertical: 6,
            }}
          >
            <TouchableOpacity
              style={{
                flex: 1,
              }}
              onPress={() => setLike(like + 1)}
            >
              <Text style={{ textAlign: "center", fontSize: 15 }}>
                <Icon name="heart-outline" size={15} />찜 {like}
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={{ flex: 1 }}
              onPress={handleChat.bind(this, row.seq)}
            >
              <Text style={{ textAlign: "center", fontSize: 15 }}>
                <Icon name="chat-outline" size={15} />
                채팅
              </Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={{ flex: 1 }}
              onPress={() => Linking.openURL(`tel:${row.num}`)}
            >
              <Text style={{ textAlign: "center", fontSize: 15 }}>
                <Icon name="cellphone" size={15} />
                전화
              </Text>
            </TouchableOpacity>
          </View>
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              backgroundColor: "#c5e1a5",
              width: userWidth - 30,
              padding: 8,
              marginTop: 12,
              borderRadius: 10,
            }}
          >
            <Text>
              <View>
                <Text style={{ color: "#424242" }}>
                  <Icon name="map-marker-radius-outline" size={15} />{" "}
                  {row.local}
                </Text>
              </View>
              {"\n"}
              <View style={{ marginTop: 15 }}>
                <Text style={{ color: "#424242" }}>
                  <Icon name="clock-outline" size={15} /> 매일 10시~9시
                </Text>
              </View>
            </Text>
          </View>
          <Text style={{ marginTop: 12, color: "#757575", lineHeight: 20 }}>
            {row.intro}
          </Text>
        </View>
        <View style={{ height: 80 }}></View>
      </ScrollView>
      <FixedBottom />
    </View>
  );
};
