import React, { useLayoutEffect, useEffect, useState } from "react";
import {
  View,
  Text,
  Button,
  Image,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  Dimensions,
} from "react-native";
import axios from "axios";
import { Card, ListCard, ShopDes, ShopTitle } from "../../assets/common/common";
import { SafeAreaView } from "react-native-safe-area-context";
import Icon from "react-native-vector-icons/MaterialCommunityIcons";
import styled, { withTheme } from "styled-components/native";
import { Rating, AirbnbRating } from "react-native-ratings";
import { createStackNavigator } from "@react-navigation/stack";
import Port from "../Port";

export const photoShopList = ({ navigation }) => {
  const [photoShops, setPhotoShops] = useState();
  const userWidth = Dimensions.get("window").width;

  useEffect(() => {
    axios({
      method: "get",
      url: `${Port}/getPhotoShop`,
      responseType: "json",
    })
      .then(({ data, status }) => {
        if (status == 200) setPhotoShops(data);
      })
      .catch((res) => {
        console.log(res);
      });
  }, []);

  return (
    <ListCard>
      <View
        style={{
          flexDirection: "row",
          width: userWidth - 30,
          padding: 12,
          marginTop: 8,
          borderRadius: 6,
          elevation: 3,
          backgroundColor: "#fff",
          shadowOffset: { width: 1, height: 1 },
          shadowOpacity: 0.3,
          marginBottom: 20,
        }}
      >
        <TouchableOpacity>
          <Text>기본</Text>
        </TouchableOpacity>
        <TouchableOpacity>
          <Text>평점 높은 곳</Text>
        </TouchableOpacity>
        <TouchableOpacity>
          <Text>가장 가까운 곳</Text>
        </TouchableOpacity>
      </View>
      {photoShops &&
        photoShops.map((val, idx) => {
          return (
            <Card
              key={val.seq}
              onPress={() => navigation.navigate("Detail", { row: val })}
            >
              <View style={{ flexDirection: "row" }}>
                <Image
                  style={{ width: 150, height: 150, borderRadius: 6 }}
                  source={{
                    uri: `${val.present_img}`,
                  }}
                />
                <View
                  style={{
                    marginLeft: 10,
                    alignItems: "baseline",
                    position: "relative",
                  }}
                >
                  <ShopTitle>{val.title}</ShopTitle>
                  <ShopDes>{val.local}</ShopDes>
                  <ShopDes>매일 10시~9시</ShopDes>
                  <AirbnbRating
                    type="custom"
                    reviews={[1, 2, 3, 4, 5]}
                    ratingBackgroundColor="#c8c7c8"
                    ratingCount={5}
                    isDisabled={true}
                    defaultRating={val.satis_avg}
                    size={15}
                  />
                </View>
              </View>
            </Card>
          );
        })}
    </ListCard>
  );
};
