import React, { useLayoutEffect, useEffect, useState } from "react";
import {
  View,
  Text,
  Button,
  Image,
  Dimensions,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
} from "react-native";
import axios from "axios";
import {
  Con,
  Card,
  SafeCon,
  ListCard,
  ShopDes,
  ShopTitle,
  ReservBtn,
  ShopDetailTitle,
} from "../assets/common/common";
import { SafeAreaView } from "react-native-safe-area-context";
import Icon from "react-native-vector-icons/MaterialCommunityIcons";
import styled, { withTheme } from "styled-components/native";
import { Rating, AirbnbRating } from "react-native-ratings";
import { createStackNavigator } from "@react-navigation/stack";
import { SwiperFlatList } from "react-native-swiper-flatlist";

const Home = ({ navigation }) => {
  const Stack = createStackNavigator();
  const [photoShops, setPhotoShops] = useState();

  const showShopDetail = (seq) => {
    alert(seq);
  };

  useEffect(() => {
    axios({
      method: "get",
      url: "http://172.30.1.25:4001/getPhotoShop",
      responseType: "json",
    })
      .then(({ data, status }) => {
        if (status == 200) setPhotoShops(data);
      })
      .catch((res) => {
        console.log(res);
      });
  }, []);

  const photoShopDetail = ({ route, navigation }) => {
    const row = route.params.row;
    const colors = [
      `${row.present_img}`,
      `${row.sub_img_1}`,
      `${row.sub_img_2}`,
      `${row.sub_img_3}`,
      `${row.sub_img_4}`,
    ];
    const userWidth = Dimensions.get("window").width;

    return (
      <ScrollView style={{ backgroundColor: "white" }}>
        <SwiperFlatList
          data={colors}
          onChangeIndex={({ index, prevIndex }) => {}}
          renderItem={({ item }) => (
            <View style={[{ backgroundColor: "black" }]}>
              <Image
                style={{ width: userWidth, height: 450 }}
                source={{ uri: item }}
              />
            </View>
          )}
        />
        <View style={{ paddingLeft: 12 }}>
          <ShopDetailTitle>{row.title}</ShopDetailTitle>
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              backgroundColor: "#c5e1a5",
              width: 300,
              padding: 8,
              marginTop: 12,
              borderRadius: 10,
            }}
          >
            {/* <Icon name="source-commit-end-local" size={30} color="#757575" /> */}
            <Text>
              <View>
                <Text style={{ color: "#424242" }}>
                  <Icon name="map-marker-radius-outline" size={15} />{" "}
                  {row.local}
                </Text>
              </View>
              {"\n"}
              <View style={{ marginTop: 15 }}>
                <Text style={{ color: "#424242" }}>
                  <Icon name="clock-outline" size={15} /> 매일 10시~9시
                </Text>
              </View>
            </Text>
          </View>
          <Text style={{ marginTop: 12, color: "#757575" }}>{row.intro}</Text>
          <View style={{ flexDirection: "row", justifyContent: "center" }}>
            <TouchableOpacity>
              <Text>전화하기</Text>
            </TouchableOpacity>
            <TouchableOpacity>
              <Text>예약하기</Text>
            </TouchableOpacity>
          </View>
        </View>
      </ScrollView>
    );
  };

  const photoShopList = ({ navigation }) => {
    return (
      <ListCard>
        {photoShops &&
          photoShops.map((val, idx) => {
            return (
              <Card
                key={val.seq}
                onPress={() => navigation.navigate("Detail", { row: val })}
              >
                <View style={{ flexDirection: "row" }}>
                  <Image
                    style={{ width: 150, height: 150, borderRadius: 6 }}
                    source={{
                      uri: `${val.present_img}`,
                    }}
                  />
                  <View
                    style={{
                      marginLeft: 10,
                      alignItems: "baseline",
                      position: "relative",
                    }}
                  >
                    <ShopTitle>{val.title}</ShopTitle>
                    <ShopDes>{val.local}</ShopDes>
                    <ShopDes>매일 10시~9시</ShopDes>
                    <AirbnbRating
                      type="custom"
                      reviews={[1, 2, 3, 4, 5]}
                      ratingBackgroundColor="#c8c7c8"
                      ratingCount={5}
                      isDisabled={true}
                      defaultRating={val.satis_avg}
                      size={15}
                    />
                  </View>
                </View>
              </Card>
            );
          })}
      </ListCard>
    );
  };

  return (
    <Stack.Navigator screenOptions={{ headerShown: false }}>
      <Stack.Screen name="List" component={photoShopList} />
      <Stack.Screen name="Detail" component={photoShopDetail} />
    </Stack.Navigator>
  );
};

const { width } = Dimensions.get("window");

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: "white" },
  child: { width, justifyContent: "center" },
  text: { fontSize: width * 0.5, textAlign: "center" },
});

export default Home;
