import React, { useLayoutEffect, useEffect, useState } from "react";
import {
  View,
  Text,
  Button,
  Image,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import axios from "axios";
import {
  Con,
  Card,
  SafeCon,
  ListCard,
  ShopDes,
  ShopTitle,
  ReservBtn,
} from "../assets/common/common";
import { SafeAreaView } from "react-native-safe-area-context";
import Icon from "react-native-vector-icons/FontAwesome";
import styled, { withTheme } from "styled-components/native";
import { Rating, AirbnbRating } from "react-native-ratings";
import { createStackNavigator } from "@react-navigation/stack";

const Home = ({ navigation }) => {
  const Stack = createStackNavigator();
  const [photoShops, setPhotoShops] = useState();

  const showShopDetail = (seq) => {
    alert(seq);
  };

  useEffect(() => {
    axios({
      method: "get",
      url: "http://172.30.1.25:4001/getPhotoShop",
      responseType: "json",
    })
      .then(({ data, status }) => {
        if (status == 200) setPhotoShops(data);
      })
      .catch((res) => {
        console.log(res);
      });
  }, []);

  const photoShopDetail = () => {
    return <Text>zz</Text>;
  };

  const photoShopList = ({ navigation }) => {
    return (
      <ListCard>
        {photoShops &&
          photoShops.map((val, idx) => {
            return (
              <Card key={val.seq} onPress={navigation.navigate("Detail")}>
                <View style={{ flexDirection: "row" }}>
                  <Image
                    style={{ width: 150, height: 150, borderRadius: 6 }}
                    source={{
                      uri: `${val.present_img}`,
                    }}
                  />
                  <View
                    style={{
                      marginLeft: 10,
                      alignItems: "baseline",
                      position: "relative",
                    }}
                  >
                    <ShopTitle>{val.title}</ShopTitle>
                    <ShopDes>{val.local}</ShopDes>
                    <ShopDes>매일 10시~9시</ShopDes>
                    <AirbnbRating
                      type="custom"
                      reviews={[1, 2, 3, 4, 5]}
                      ratingBackgroundColor="#c8c7c8"
                      ratingCount={5}
                      isDisabled={true}
                      defaultRating={val.satis_avg}
                      size={15}
                    />
                  </View>
                </View>
              </Card>
            );
          })}
      </ListCard>
    );
  };

  return (
    <Stack.Navigator screenOptions={{ headerShown: false }}>
      <Stack.Screen name="List" component={photoShopList} />
      <Stack.Screen name="Detail" component={photoShopDetail} />
    </Stack.Navigator>
  );
};

export default Home;
